import styled from 'styled-components';

export const Container = styled.div`
  border-radius:4px;
  background:#FFFFFF;
  margin-left:35px;
`;

export const Title = styled.h3`
  font-size: 1.5em;
  color: #161d25;
  font-weight: bold;
  font: Bold 24px/36px;
`;
export const Content = styled.div`
  flex: 1 1 auto;
  display: flex;
  flex-direction: column;
`;
